CREATE TABLE IF NOT EXISTS `city_states` (
  `ID` int(5) NOT NULL AUTO_INCREMENT,
  `NAME` text NOT NULL,
  `UNIQUE_ABILITY` text NOT NULL,
  `HISTORICAL_CONTEXT` text NOT NULL,
  `IMAGE` text NOT NULL,
  `TYPE` text NOT NULL,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4 ;

--
-- Дамп данных таблицы `city_states`
--

INSERT INTO `city_states` (`ID`, `NAME`, `UNIQUE_ABILITY`, `HISTORICAL_CONTEXT`, `IMAGE`, `TYPE`) VALUES
(1, 'Валетта', 'Военный город-государство<br>\r\n+2 [ICON_Production] производства в [ICON_Capital] столице при создании юнитов.<br>\r\n+2 [ICON_Production] производства для каждого военного лагеря при строительстве юнитов.<br>\r\nЕще +2 [ICON_Production] производства для каждого военного лагеря при строительстве юнитов.\r\n<br><br>\r\nБонус сюзерена Валлетты <br>\r\nЗдания в центре города и в военном лагере можно покупать за [ICON_Faith] веру. Снижена стоимость стен Древнего мира, Средневековья и эпохи Возрождения, но купить их можно только за [ICON_Faith] веру.', 'Мальтийский город Валлетта был основан в 1566 году Жаном Паризо де Валеттом, 49-м Грандмастером Суверенного Военного Ордена Госпитальеров Святого Иоанна Иерусалимского, Родоса и Мальты. Годом ранее госпитальерам пришлось выдержать осаду и вторжение турок-османов (Мальту передал иоаннитам в вечное пользование Карл V Испанский в 1530 году, надеясь, что об этот своеобразный мол разобьется мутная волна османских набегов).\r\n<br><br>\r\nЧтобы построить и укрепить новый город на острове, Валетт попросил помощи у правителей христианского мира. И он ее получил... очевидно, христианские лидеры считали, что лучше пусть дорогу мусульманам преграждают воинственные фанатики, чем их собственные войска и корабли. Папа Пий V послал своего военного архитектора для проектирования укреплений, а король Испании Филипп II дал корабли и оружие. Другие жертвовали большие суммы денег, финансируя строительство церквей, казарм и портовых сооружений.\r\n<br><br>\r\nБлагодаря прекрасной гавани и стратегическому местоположению, город пережил столетия войн и даже дотянул до Второй мировой. Рыцари занялись охраной средиземноморских морских путей между христианской Европой и Левантом, отражая атаки берберских корсаров и других пиратов. Кроме того, они освобождали рабов-христиан из мусульманского плена, нападая на османские суда. Но возвышение Генуи, Венеции и Пизы, а позднее протестантская Реформация, значительно сократили финансирование Валетты.\r\n<br><br>\r\nВ 1798 году, когда по пути в Египет Наполеон захватил Мальту, озабоченность выразили только британцы и Неаполь. Пару лет спустя мальтийцы подняли восстание против французского гарнизона, и победившие вожди повстанцев передали остров англичанам. Парижский договор 1814 года закрепил права на остров за британцами. Все изменилось лишь в 1964 году, когда Мальта обрела независимость.\r\n', '<img src="./StartPage_files/images/content/1.png" alt="альтернативный текст">', 'Военный'),
(2, 'CityStates NAME 2', 'CityStates UNIQUE_ABILITY 2', 'CityStates HISTORICAL_CONTEXT 2', '<img src="./StartPage_files/images/content/1.png" alt="альтернативный текст">', 'CityStates TYPE 2'),
(3, 'NAME3', 'UNIQUE_ABILITY3', 'HISTORICAL_CONTEXT3', 'IMAGE3', 'TYPE3');

