CREATE TABLE IF NOT EXISTS `concepts` (
  `TEXT` text NOT NULL,
  `NAME` text NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

--
-- Дамп данных таблицы `concepts`
--

INSERT INTO `concepts` (`TEXT`, `NAME`) VALUES
('<div class="contentHeader"> <br> Введение </div>\n <br>\n<p style="color:rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\nГорода крайне важны для успешного развития вашей цивилизации. Именно здесь возникают юниты, здания и чудеса. Город позволяет исследовать новые технологии и накапливать богатства. Без мощных, хорошо расположенных городов невозможно добиться победы.\n</p>\n\n', 'Введение'),
('<div class="contentHeader"> <br> Как строить города </div>\n <br>\n<p style="color:rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\nГорода основывают поселенцы. Если поселенцы оказываются на клетке, на которой можно заложить город, то появится кнопка действия «Основать город». Щелкните по ней, и поселенцы исчезнут, а на их месте появится новый город.\n</p>\n', 'Как строить города'),
('<div class="contentHeader"> <br> Рост </div>\n <br>\n<p style="color:rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\nВаш город и его [ICON_Citizen] население будут расти при избытке [ICON_Food] пищи. Любое количество [ICON_Food] пищи по отношению к потреблению [ICON_Citizen] горожанами, превышающее 2 ед. на каждого, считается излишком и ускоряет прирост [ICON_Citizen] населения.\n<br>\n<br>\n&#8194; &#8194; Город использует все излишки [ICON_Food] пищи для стимулирования роста, если количество его нынешнего [ICON_Citizen] населения меньше лимита [ICON_Housing] жилья на 2 или больше. Если [ICON_Citizen] население города ниже лимита [ICON_Housing] жилья на 1, его рост замедлится до 50%. Когда [ICON_Citizen] население достигнет лимита [ICON_Housing] жилья, его рост составит только 25% от нормальной скорости. В таком случае в городе может быть до 5 [ICON_Citizen] горожан сверх лимита [ICON_Housing] жилья, а затем он перестанет расти.\n<br><br>\n&#8194; &#8194; В городе начнется застой и он перестанет расти, если не получит излишков [ICON_Food] пищи. Если он станет получать меньше [ICON_Food] пищи, чем требуется его [ICON_Citizen] горожанам, то наступит голод, и [ICON_Citizen] население будет уменьшаться, пока запасов еды не будет хватать на всех.\n</p>\n', 'Рост'),
('<div class="contentHeader"> <br> Пища </div>\n <br>\n<p style="color:rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\nГороду требуется 2 единицы [ICON_Food] пищи на каждого [ICON_Citizen] горожанина (еще один синоним [ICON_Citizen] «населения») за один ход, чтобы избежать голода. Город получает [ICON_Food] пищу (а также другую продукцию), отправляя своих [ICON_Citizen] горожан «работать» на землях вокруг города. Город может обрабатывать любую клетку при условии, что она в пределах границ цивилизации и не далее 3-х клеток от него, а также не обрабатывается другим городом. Города не могут обрабатывать клетки, занятые чудесами.\n    <br><br>\n&#8194; &#8194; Город, предоставленный сам себе, будет отправлять столько [ICON_Citizen] горожан, сколько нужно, чтобы обеспечить себя [ICON_Food] пищей.\n   </p>\n', 'Пища'),
('<div class="contentHeader"> <br> Ландшафт и его особенности </div>\n <br>\n<p style="color:rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\nВ Civilization VI мир состоит из шестиугольных клеток (также иногда называемых плитками или сотами). Эти клетки характеризуются различными типами ландшафта: это могут быть пустыни, равнины, луга, холмы и т.д. На многих из них встречаются такие особенности, как обычные или тропические леса. От этих элементов зависит, насколько полезной может быть клетка для соседнего города и насколько сложно юнитам по ней передвигаться. Ландшафт и особенности клетки оказывают существенное влияние на ход боевых действий на ней.\n<br><br>\n&#8194; &#8194; Каждая клетка в игре принадлежит какому-то континенту. Географические границы континентов не обязательно совпадают с границами суши – карта Пангеи, например, состоит из четырех разных континентов. Узнать, к какому континенту принадлежит клетка, можно по всплывающем временному указателю клетки или с помощью линзы континентов.   </p>\n', 'Ландшафт');

