CREATE TABLE IF NOT EXISTS `introduction` (
  `ID` int(5) NOT NULL AUTO_INCREMENT,
  `INTR_NAME` text NOT NULL,
  `INTR_TEXT` text NOT NULL,
  PRIMARY KEY (`ID`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 AUTO_INCREMENT=4 ;

--
-- Дамп данных таблицы `introduction`
--

INSERT INTO `introduction` (`ID`, `INTR_NAME`, `INTR_TEXT`) VALUES
(1, 'Понятия', '<center><img src="./StartPage_files/images\\\\introduction/title.png" alt="Введение"></center><br><div class="contentSubheader"> Введение </div>														<p style="margin-top:4px; color:  rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">Добро пожаловать в Цивилопедию – кладезь знаний по игре Civilization VI Сида Мейера! Здесь вы найдете подробное описание всех аспектов игры. С помощью закладок в верхней части экрана можно перемещаться по разделам Цивилопедии. На панели навигации слева показаны статьи, входящие в состав раздела. Щелкните на статье, чтобы открыть ее.\n</p>'),
(2, 'Основные цивилизации', '<div class="contentHeader"> <br> Введение </div>                                                <br>															<p style="margin-top:4px; color:  rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">Каждая цивилизация уникальна, у каждой свои особенности, свои боевые юниты, своя инфраструктура. Есть свои особенности и у лидеров цивилизаций – некоторые проявляются в самом начале игры, другие можно в полной мере оценить лишь позже. Вы можете просмотреть все особенности цивилизаций, их лидеров, боевых юнитов и инфраструктуры при выборе цивилизации.</p>'),
(3, 'Лидеры', '<div class="contentHeader"><br>Введение </div>                                                <br>															<p style="margin-top:4px; color:  rgba(58, 56, 56, 0.8); text-indent: 20px; font-size:16px;">\\\\nУ каждой цивилизации есть лидер. У некоторых цивилизаций только один возможный лидер, у других есть несколько вариантов. Как и у цивилизаций, у всех лидеров есть уникальные особенности, они свои для каждого лидера, даже если это лидеры одной и той же цивилизации. У каждого лидера свои цели, базирующиеся на характере исторической личности, а также некоторые скрытые, случайные цели.\\\\n</p>');

